exports.start = function() {

    var WebSocket = require('ws');
    var ws = new WebSocket('ws://localhost:8080');


    ws.on('open', function() {
        console.log("connected");
        ws.send(JSON.stringify({
            action: 'auth',
            username: 'test',
            password: 'install'
        }));
        ws.on('message', function(message) {
            console.log("received:");
            console.log(message);
        });
    });
};
