var io = require('socket.io-client')
;
var socket = io.connect('http://localhost:8080');

socket.on('connect', function() {
    console.log("connected");
    socket.send(JSON.stringify({
        action: 'auth',
        username: 'test',
        password: 'install'
    }));
    socket.on('message', function(message) {
        console.log("received:");
        console.log(message);
    })
});