/**
 * @module ompimon
 * @submodule ompimon-protocol
 */

var
    util = require("util"),
    _ = require("underscore"),
    eventEmitter = require("events").EventEmitter
;

module.exports = Action;

/**
 *
 * @class Action
 * @namespace Ompimon.Protocol.ClientAction
 * @extends EventEmitter
 * @constructor
 */
function Action() {
    eventEmitter.call(this);
}

util.inherits(Action, eventEmitter);


_.extend(Action.prototype, {
    /**
     * Whether this action needs authentication
     * @property needAuthentication
     * @type boolean
     * @default true
     */
    needAuthentication: true

    /**
     * @event send
     * @param client {Websocket}
     * @param message {Buffer}
     */
});