var
    BufferBuilder = require("buffer-builder"),
    _ = require("underscore"),
    binary = require("binary"),
    Int64 = require('node-int64'),

    Client = require("ompimon-client/client"),

    Protocol = require("../.."),
    auth = require("ompimon-auth").auth,
    Parser = require("../../parser"),
    stub = require("../../test-client-stub"),

    action = require("../../client_actions/Applications")

    ;
auth.type = "test";

var Action = new action;

var client = new Client('application', new Buffer(256));

module.exports = {
    testParse: function(test) {
        var data = stub.buildApplication(stub.applicationData);
        var parser = new Parser(data);

        var result = Action.parse(client, parser);

        test.deepEqual(result, stub.applicationData);
        test.done();
    },

    testProcess: function(test) {
        Action.once('send', function (client, data) {
            test.done();
        });

        Action.process(stub.applicationData);
    }
};