var
    BufferBuilder = require("buffer-builder"),
    _ = require("underscore"),
    binary = require("binary"),
    Int64 = require('node-int64'),

    cluster = require('ompimon-cluster'),
    protocol = require("../../main"),
    protoData = require('../../cluster_actions/Data'),
    Parser = require("../../parser"),

    stub = require("../../test-stub")
    ;


var client = stub.getTestClient("Data");

var action = new protoData();


module.exports = {
    testParse: function(test) {
        var parser = new Parser(stub.buildSend(stub.sendData).get());

        var result = action.parse(client, parser);

        test.deepEqual(result, stub.sendData);

        test.done();
    }
};
