var
    BufferBuilder = require("buffer-builder"),
    _ = require("underscore"),
    binary = require("binary"),
    Int64 = require('node-int64'),

    cluster = require('ompimon-cluster'),
    protocol = require("../../main"),
    protoData = require('../../cluster_actions/TotalCounter'),
    Parser = require("../../parser"),

    stub = require("../../test-stub")
    ;

var client = cluster.client;

client = new client(new Buffer(256));
client.id = "1234567890yyyasdfasdfasdfasdfasd";
client.counterFunctions = ['broadcast', 'barrier'];
client.ranks = [1, 2];

var action = new protoData();

module.exports = {
    testParse: function(test) {
        var parser = new Parser(stub.buildTotalCounter(stub.totalCounter).get());

        var result = action.parse(client, parser);

        test.deepEqual(result, stub.totalCounter);

        test.done();
    }
};
