    /**
     * @method getApplications
     * @param callback {Function}
     * @async
     */
    getApplications: function(callback){
        var self = this;
        rclient.LRANGE("apps",0, -1, function(err, list){
            var ret = [];
            var tasks = [];
            _.each(list, function(appId){
                tasks.push(function(callback) {
                    self.isFinalize(appId, function(err, finalize){
                        console.log(finalize);
                        if(!finalize){
                            ret.push(appId);
                        }
                        callback();
                    });
                });
            });

            async.parallel(tasks, function(){
                callback(err, ret);
            });
        });
    },

