var _ = require("underscore")._,
    redis = require("redis"),
    rclient = redis.createClient(),
    Int64 = require('node-int64');

var expireTime = 1800; //30 min

exports.clearAllData = function(callback){
    _deleteKeys("app*",callback);
};


