    /**
     * for Protocol ActionId 0x02
     *
     * @param appId ID of the Application
     * @param data received data
     * @param callback
     */
    receiveAction: function(appId, data, callback){
        var self = this;
        var tasks = [];
        data.forEach(function(rankData){
            _.each(rankData.counters, function(countVal, funcName) {
                tasks.push(function(callback) {
                    self._setCounterData(appId, rankData.rank, funcName, countVal, callback);
                });
            });

            rankData.data.forEach(function(sendData){
                self._setSendData(appId, rankData.rank, sendData, function(){});
                tasks.push(function(callback) {
                    self._setSendData(appId, rankData.rank, sendData, callback);
                });
            });
        });


        async.parallel(tasks, callback);
    },

