var
    storage = require("../"),
    rclient = storage.getConnection(),
    Int64 = require('node-int64');

module.exports = {
    testCounterFunction: function(test){
        var counter = [
            "broadcast",
            "barrier"
        ];
        storage.getCounterFunctions(0,function(list){
            test.deepEqual(counter, list, "Wrong order for Counter functions");
            test.done();
        });

    },
    testSendFunction: function(test){
        var sends = [
            "ibsend",
            "bsend",
            "irsend"
        ];
        storage.getSendFunctions(0,function(list){
            test.deepEqual(sends, list, "Wrong order for Send functions");
            test.done();
        });
    },
    testQuitConnection: function(test){
        storage.killConnection();
        test.done();
    }
};