var
    storage = require("../"),
    rclient = storage.getConnection();

var appId = 0;

module.exports = {
    testFinalize: function(test){
        storage.finalize(appId, function(){
            test.done();
        });
    },
    testIsFinalize: function(test){
        storage.isFinalize(appId, function(err, finalize){
            test.equal('true', finalize, "Wrong value for finalize");
            test.done();
        });
    }
};